package fr.xpdustry.distributor.command.param.string;import fr.xpdustry.distributor.command.param.*;import io.leangen.geantyref.*;public class StringParameter extends CommandParameter<String>{    public StringParameter(String name, String defaultValue, boolean optional, String delimiter, ArgumentPreprocessor<String> parser){        super(name, defaultValue, optional, delimiter, TypeToken.get(String.class), parser);    }    public StringParameter(String name, String defaultValue, boolean optional, String delimiter){        this(name, defaultValue, optional, delimiter, ArgumentPreprocessor.RAW);    }    public StringParameter(String name, String defaultValue, boolean optional, ArgumentPreprocessor<String> parser){        super(name, defaultValue, optional, null, TypeToken.get(String.class), parser);    }    public StringParameter(String name, String defaultValue, boolean optional){        this(name, defaultValue, optional, null, ArgumentPreprocessor.RAW);    }}